def input_left(pressed):
    global current_index

    pm.feed()
    if pressed:
        rgb.setbrightness(rgb.getbrightness() - 2)


def input_right(pressed):
    global current_index

    pm.feed()
    if pressed:
        rgb.setbrightness(rgb.getbrightness() + 2)

def start():
    buttons.register(defines.BTN_LEFT, input_left)
    buttons.register(defines.BTN_RIGHT, input_right)

    populate_apps()
    render_current_app()


